package com.exercise.java;

public class Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int evenNumber = 0;
		for (int i=0; i<=20; i++) {
			// System.out.println(i);
			if(evenNumber%2==0) {
				System.out.println(i + " is even number");
			}
			evenNumber ++;
		}
		
	}

}
